import { useState } from 'react'

export default function SingleQuestion({ question, answer }){
    return(
        <div className='qna'>
            <article className="article-question">
                <h4 className="">{question}</h4>
                <FaChevronDown className='icon'/>
            </article>
            <p className='answer'>{answer}</p>
        </div>
    )
}
